import React from 'react';
import {View, StyleSheet, Text} from 'react-native';

const Somar = (props) =>{

    let volume = props.value;
    let somar=0;
    for(let i=0; i<volume.length; i++){
      somar = somar + volume[i].volume;
    }
    if(somar > 475){
      return(
        <View>
          <Text>Valor Maximo do copo excedido favor diminuir a quantidade para abaixo de 475ML</Text>
          <Text>Soma Atual: {somar}</Text>
        </View>
      );
    } else {
      return(<Text>Total: {somar}ML</Text>);
    }

  }


  export default Somar;
