import React from 'react';
import { View, Text } from 'react-native';

export default class MostrarSlider extends React.Component{

    render (){
        if(this.props.status){
        return(
            <View style={{borderColor: "#006eff", borderWidth: 5, borderRadius: 50, margin:5}}>
                <Slider
                style={{height: 30,borderRadius: 20,margin:5, backgroundColor: "#006eff"}}
                minimumValue={0}
                maximumValue={1}
                minimumTrackTintColor="#006eff"
                maximumTrackTintColor="#ffffff"
                thumbTintColor = "#8c8c8c"
                />
            </View>
            );
        }
    }
}
